    private void btnRegistroRActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnRegistroRActionPerformed
        UsuarioData usuarioD = new UsuarioData();
        Usuario usr = new Usuario();
        String user = txtNombreR.getText();
        String pass = usuarioD.cifrar("Key", txtContraR.getText());
        if (!txtNombreR.getText().equals("") && !txtContraR.getText().equals("")) {
            usr.setNombreUsuario(user);
            usr.setContrasenna(pass);
            usr.setCarpeta(user);
            if (data.registra(usr)) {
                lblAvisoR.setText(user + " registrado correctamente");
            } else {
                lblAvisoR.setText("Error en ingresar");
            }
        }
    }//GEN-LAST:event_btnRegistroRActionPerformed

